import{_ as o,c as i,o as e,ag as r}from"./chunks/framework.Bw-5EFTY.js";const m=JSON.parse('{"title":"HLDE - Diagnósticos y Laboratorios: Estudios de Imagen","description":"","frontmatter":{},"headers":[],"relativePath":"modulos-clinicos/03-diagnosticos/estudios-imagen.md","filePath":"modulos-clinicos/03-diagnosticos/estudios-imagen.md"}'),n={name:"modulos-clinicos/03-diagnosticos/estudios-imagen.md"};function l(t,a,s,d,c,g){return e(),i("div",null,a[0]||(a[0]=[r('<h1 id="hlde-diagnosticos-y-laboratorios-estudios-de-imagen" tabindex="-1">HLDE - Diagnósticos y Laboratorios: Estudios de Imagen <a class="header-anchor" href="#hlde-diagnosticos-y-laboratorios-estudios-de-imagen" aria-label="Permalink to &quot;HLDE - Diagnósticos y Laboratorios: Estudios de Imagen&quot;">​</a></h1><h2 id="informacion-del-paciente" tabindex="-1">Información del Paciente <a class="header-anchor" href="#informacion-del-paciente" aria-label="Permalink to &quot;Información del Paciente&quot;">​</a></h2><ul><li><strong>Nombre</strong>: Dylan Antonio Martínez Labastida</li><li><strong>Expediente</strong>: 345370</li><li><strong>Edad</strong>: 8 años, 8 meses</li><li><strong>Periodo de estudio</strong>: 01.07.25 - 07.07.25</li></ul><hr><h2 id="cronologia-de-estudios-de-imagen" tabindex="-1">Cronología de Estudios de Imagen <a class="header-anchor" href="#cronologia-de-estudios-de-imagen" aria-label="Permalink to &quot;Cronología de Estudios de Imagen&quot;">​</a></h2><h3 id="_01-07-25-radiografia-de-abdomen-extra-hnp" tabindex="-1">01.07.25 - Radiografía de Abdomen (Extra-HNP) <a class="header-anchor" href="#_01-07-25-radiografia-de-abdomen-extra-hnp" aria-label="Permalink to &quot;01.07.25 - Radiografía de Abdomen (Extra-HNP)&quot;">​</a></h3><p><strong>Indicación</strong>: Evaluación de dolor abdominal postquirúrgico</p><h4 id="hallazgos" tabindex="-1">Hallazgos <a class="header-anchor" href="#hallazgos" aria-label="Permalink to &quot;Hallazgos&quot;">​</a></h4><ul><li><strong>Aire libre subdiafragmático</strong> ⚠️ <strong>CRÍTICO</strong></li><li>Evidencia de perforación intestinal</li><li>Indicación quirúrgica urgente</li></ul><h4 id="interpretacion-clinica" tabindex="-1">Interpretación Clínica <a class="header-anchor" href="#interpretacion-clinica" aria-label="Permalink to &quot;Interpretación Clínica&quot;">​</a></h4><p>🔴 <strong>Pneumoperitoneo</strong>: Confirma complicación postquirúrgica con perforación intestinal. Requiere exploración quirúrgica inmediata.</p><hr><h3 id="_04-07-25-ultrasonido-abdominal-extra-hnp" tabindex="-1">04.07.25 - Ultrasonido Abdominal (Extra-HNP) <a class="header-anchor" href="#_04-07-25-ultrasonido-abdominal-extra-hnp" aria-label="Permalink to &quot;04.07.25 - Ultrasonido Abdominal (Extra-HNP)&quot;">​</a></h3><p><strong>Indicación</strong>: Búsqueda de colecciones abdominales</p><h4 id="hallazgos-morfologicos" tabindex="-1">Hallazgos Morfológicos <a class="header-anchor" href="#hallazgos-morfologicos" aria-label="Permalink to &quot;Hallazgos Morfológicos&quot;">​</a></h4><ul><li><strong>Edema intersasa regional</strong></li><li><strong>Asas intestinales dilatadas</strong></li><li><strong>Contenido predominantemente líquido</strong></li><li><strong>Ausencia de peristalsis visible</strong> durante el estudio</li></ul><h4 id="hallazgos-renales" tabindex="-1">Hallazgos Renales <a class="header-anchor" href="#hallazgos-renales" aria-label="Permalink to &quot;Hallazgos Renales&quot;">​</a></h4><ul><li><strong>Hidronefrosis bilateral</strong> ⚠️ <strong>SIGNIFICATIVO</strong></li></ul><h4 id="interpretacion-clinica-1" tabindex="-1">Interpretación Clínica <a class="header-anchor" href="#interpretacion-clinica-1" aria-label="Permalink to &quot;Interpretación Clínica&quot;">​</a></h4><p>🔶 <strong>Íleo paralítico</strong> con distensión intestinal y edema intersasa<br> 🔶 <strong>Hidronefrosis bilateral</strong> requiere seguimiento urológico<br> 🔶 <strong>Líquido libre abdominal</strong> compatible con proceso inflamatorio</p><hr><h3 id="_06-07-25-radiografia-de-abdomen-hnp" tabindex="-1">06.07.25 - Radiografía de Abdomen (HNP) <a class="header-anchor" href="#_06-07-25-radiografia-de-abdomen-hnp" aria-label="Permalink to &quot;06.07.25 - Radiografía de Abdomen (HNP)&quot;">​</a></h3><p><strong>Indicación</strong>: Evaluación preoperatoria</p><h4 id="proyecciones" tabindex="-1">Proyecciones <a class="header-anchor" href="#proyecciones" aria-label="Permalink to &quot;Proyecciones&quot;">​</a></h4><ul><li><strong>Abdomen de pie</strong>: Mala distribución de aire, escaso contenido gaseoso</li><li><strong>Abdomen tangencial</strong>: Niveles hidroaéreos sin aire libre</li><li><strong>Abdomen portátil</strong>: Confirmación de hallazgos</li></ul><h4 id="hallazgos-1" tabindex="-1">Hallazgos <a class="header-anchor" href="#hallazgos-1" aria-label="Permalink to &quot;Hallazgos&quot;">​</a></h4><ul><li><strong>Mala distribución de aire intestinal</strong></li><li><strong>Niveles hidroaéreos</strong> presentes</li><li><strong>Ausencia de aire libre</strong> subdiafragmático</li><li><strong>Escaso contenido gaseoso</strong> intestinal</li></ul><h4 id="interpretacion-clinica-2" tabindex="-1">Interpretación Clínica <a class="header-anchor" href="#interpretacion-clinica-2" aria-label="Permalink to &quot;Interpretación Clínica&quot;">​</a></h4><p>🔶 <strong>Íleo mecánico vs paralítico</strong> con obstrucción intestinal parcial<br> ✅ <strong>Sin pneumoperitoneo</strong> en este momento<br> 🔶 <strong>Distensión intestinal</strong> compatible con proceso obstructivo</p><hr><h3 id="_07-07-25-radiografia-de-abdomen-ap-y-tangencial" tabindex="-1">07.07.25 - Radiografía de Abdomen AP y Tangencial <a class="header-anchor" href="#_07-07-25-radiografia-de-abdomen-ap-y-tangencial" aria-label="Permalink to &quot;07.07.25 - Radiografía de Abdomen AP y Tangencial&quot;">​</a></h3><p><strong>Indicación</strong>: Control postquirúrgico inmediato</p><h4 id="hallazgos-tecnicos" tabindex="-1">Hallazgos Técnicos <a class="header-anchor" href="#hallazgos-tecnicos" aria-label="Permalink to &quot;Hallazgos Técnicos&quot;">​</a></h4><ul><li><strong>Imagen velada</strong> (calidad técnica limitada)</li><li><strong>Presencia de sonda nasogástrica</strong> a cámara gástrica</li><li><strong>Artefactos por material quirúrgico</strong></li></ul><h4 id="hallazgos-abdominales" tabindex="-1">Hallazgos Abdominales <a class="header-anchor" href="#hallazgos-abdominales" aria-label="Permalink to &quot;Hallazgos Abdominales&quot;">​</a></h4><ul><li><strong>Niveles hidroaéreos</strong> en cuadrante superior derecho</li><li><strong>Borramiento del psoas</strong> ⚠️ <strong>SIGNIFICATIVO</strong></li><li><strong>Distensión gaseosa persistente</strong></li></ul><h4 id="interpretacion-clinica-3" tabindex="-1">Interpretación Clínica <a class="header-anchor" href="#interpretacion-clinica-3" aria-label="Permalink to &quot;Interpretación Clínica&quot;">​</a></h4><p>🔴 <strong>Borramiento del psoas</strong>: Sugiere proceso inflamatorio retroperitoneal<br> 🔶 <strong>Niveles hidroaéreos</strong>: Persistencia de íleo postquirúrgico<br> 🔶 <strong>Sonda nasogástrica</strong>: Descompresión gástrica adecuada</p><hr><h3 id="_07-07-25-tomografia-de-torax-simple" tabindex="-1">07.07.25 - Tomografía de Tórax Simple <a class="header-anchor" href="#_07-07-25-tomografia-de-torax-simple" aria-label="Permalink to &quot;07.07.25 - Tomografía de Tórax Simple&quot;">​</a></h3><p><strong>Indicación</strong>: Evaluación de estado cardiopulmonar en paciente crítico</p><h4 id="hallazgos-cardiacos" tabindex="-1">Hallazgos Cardíacos <a class="header-anchor" href="#hallazgos-cardiacos" aria-label="Permalink to &quot;Hallazgos Cardíacos&quot;">​</a></h4><ul><li><strong>Corazón de morfología normal</strong></li><li><strong>Sin crecimientos cavitarios</strong></li><li><strong>Catéter venoso central</strong> desde subclavia derecha a aurícula derecha</li><li><strong>Posición adecuada del catéter</strong></li></ul><h4 id="hallazgos-mediastinales" tabindex="-1">Hallazgos Mediastinales <a class="header-anchor" href="#hallazgos-mediastinales" aria-label="Permalink to &quot;Hallazgos Mediastinales&quot;">​</a></h4><ul><li><strong>Mediastino de dimensiones normales</strong></li><li><strong>Sin crecimientos ganglionares</strong></li><li><strong>Sin lesiones tumorales</strong></li><li><strong>Grandes vasos normales</strong> (en fase simple)</li></ul><h4 id="hallazgos-pulmonares" tabindex="-1">Hallazgos Pulmonares <a class="header-anchor" href="#hallazgos-pulmonares" aria-label="Permalink to &quot;Hallazgos Pulmonares&quot;">​</a></h4><ul><li><strong>Engrosamiento intersticial</strong> únicamente periférico</li><li><strong>Atelectasias posteriores</strong>: <ul><li>Lado derecho: desde región apical</li><li>Bilaterales: a nivel medio e inferior</li></ul></li><li><strong>Derrame pleural bilateral</strong> con predominio izquierdo ⚠️</li><li><strong>Tráquea</strong>: Presencia de sonda endotraqueal</li></ul><h4 id="hallazgos-oseos-y-partes-blandas" tabindex="-1">Hallazgos Óseos y Partes Blandas <a class="header-anchor" href="#hallazgos-oseos-y-partes-blandas" aria-label="Permalink to &quot;Hallazgos Óseos y Partes Blandas&quot;">​</a></h4><ul><li><strong>Estructuras óseas</strong>: Densidad conservada</li><li><strong>Partes blandas</strong>: Imagen lineal desde región clavicular derecha (catéter)</li></ul><h4 id="interpretacion-clinica-4" tabindex="-1">Interpretación Clínica <a class="header-anchor" href="#interpretacion-clinica-4" aria-label="Permalink to &quot;Interpretación Clínica&quot;">​</a></h4><p>🔶 <strong>Derrame pleural bilateral</strong>: Secundario a proceso inflamatorio sistémico<br> 🔶 <strong>Atelectasias bilaterales</strong>: Compatibles con ventilación mecánica prolongada<br> ✅ <strong>Catéter venoso central</strong>: Posición adecuada en aurícula derecha<br> 🔶 <strong>Engrosamiento intersticial</strong>: Posible edema pulmonar leve</p><hr><h3 id="_07-07-25-radiografia-de-torax-y-abdomen-simple" tabindex="-1">07.07.25 - Radiografía de Tórax y Abdomen Simple <a class="header-anchor" href="#_07-07-25-radiografia-de-torax-y-abdomen-simple" aria-label="Permalink to &quot;07.07.25 - Radiografía de Tórax y Abdomen Simple&quot;">​</a></h3><p><strong>Indicación</strong>: Control integral postquirúrgico</p><h4 id="hallazgos-toracicos" tabindex="-1">Hallazgos Torácicos <a class="header-anchor" href="#hallazgos-toracicos" aria-label="Permalink to &quot;Hallazgos Torácicos&quot;">​</a></h4><ul><li><strong>Campos pulmonares</strong>: Adecuada ventilación bilateral</li><li><strong>Catéter venoso central</strong>: Posición correcta</li><li><strong>Sonda endotraqueal</strong>: Posición adecuada</li></ul><h4 id="hallazgos-abdominales-1" tabindex="-1">Hallazgos Abdominales <a class="header-anchor" href="#hallazgos-abdominales-1" aria-label="Permalink to &quot;Hallazgos Abdominales&quot;">​</a></h4><ul><li><strong>Sonda nasogástrica</strong>: Descompresión gástrica</li><li><strong>Distensión intestinal</strong>: Persistente</li><li><strong>Abdomen abierto</strong>: Post-laparotomía exploradora</li></ul><hr><h2 id="resumen-de-evolucion-imagenologica" tabindex="-1">Resumen de Evolución Imagenológica <a class="header-anchor" href="#resumen-de-evolucion-imagenologica" aria-label="Permalink to &quot;Resumen de Evolución Imagenológica&quot;">​</a></h2><h3 id="cronologia-de-hallazgos-criticos" tabindex="-1">Cronología de Hallazgos Críticos <a class="header-anchor" href="#cronologia-de-hallazgos-criticos" aria-label="Permalink to &quot;Cronología de Hallazgos Críticos&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Fecha</th><th>Estudio</th><th>Hallazgo Principal</th><th>Significado Clínico</th></tr></thead><tbody><tr><td><strong>01.07.25</strong></td><td>RX Abdomen</td><td><strong>Aire libre subdiafragmático</strong></td><td>Perforación intestinal</td></tr><tr><td><strong>04.07.25</strong></td><td>USG Abdomen</td><td><strong>Hidronefrosis bilateral</strong></td><td>Compromiso renal</td></tr><tr><td><strong>06.07.25</strong></td><td>RX Abdomen</td><td><strong>Niveles hidroaéreos</strong></td><td>Íleo mecánico</td></tr><tr><td><strong>07.07.25</strong></td><td>RX Abdomen</td><td><strong>Borramiento del psoas</strong></td><td>Inflamación retroperitoneal</td></tr><tr><td><strong>07.07.25</strong></td><td>TAC Tórax</td><td><strong>Derrame pleural bilateral</strong></td><td>Respuesta inflamatoria sistémica</td></tr></tbody></table><h3 id="evolucion-del-proceso-inflamatorio" tabindex="-1">Evolución del Proceso Inflamatorio <a class="header-anchor" href="#evolucion-del-proceso-inflamatorio" aria-label="Permalink to &quot;Evolución del Proceso Inflamatorio&quot;">​</a></h3><h4 id="fase-aguda-01-07-25" tabindex="-1">Fase Aguda (01.07.25) <a class="header-anchor" href="#fase-aguda-01-07-25" aria-label="Permalink to &quot;Fase Aguda (01.07.25)&quot;">​</a></h4><ul><li><strong>Pneumoperitoneo</strong>: Perforación intestinal confirmada</li><li><strong>Indicación quirúrgica</strong>: Exploración inmediata</li></ul><h4 id="fase-de-complicaciones-04-07-25" tabindex="-1">Fase de Complicaciones (04.07.25) <a class="header-anchor" href="#fase-de-complicaciones-04-07-25" aria-label="Permalink to &quot;Fase de Complicaciones (04.07.25)&quot;">​</a></h4><ul><li><strong>Líquido libre abdominal</strong>: Proceso séptico</li><li><strong>Hidronefrosis bilateral</strong>: Compromiso renal</li><li><strong>Íleo paralítico</strong>: Disfunción intestinal</li></ul><h4 id="fase-postquirurgica-06-07-07-25" tabindex="-1">Fase Postquirúrgica (06-07.07.25) <a class="header-anchor" href="#fase-postquirurgica-06-07-07-25" aria-label="Permalink to &quot;Fase Postquirúrgica (06-07.07.25)&quot;">​</a></h4><ul><li><strong>Íleo persistente</strong>: Disfunción intestinal postoperatoria</li><li><strong>Borramiento del psoas</strong>: Inflamación retroperitoneal</li><li><strong>Derrame pleural</strong>: Respuesta sistémica</li></ul><hr><h2 id="correlacion-radiologico-clinica" tabindex="-1">Correlación Radiológico-Clínica <a class="header-anchor" href="#correlacion-radiologico-clinica" aria-label="Permalink to &quot;Correlación Radiológico-Clínica&quot;">​</a></h2><h3 id="hallazgos-que-correlacionan-con-sepsis-abdominal" tabindex="-1">Hallazgos que Correlacionan con Sepsis Abdominal <a class="header-anchor" href="#hallazgos-que-correlacionan-con-sepsis-abdominal" aria-label="Permalink to &quot;Hallazgos que Correlacionan con Sepsis Abdominal&quot;">​</a></h3><ol><li><strong>Aire libre subdiafragmático</strong> → Perforación intestinal</li><li><strong>Líquido libre abdominal</strong> → Peritonitis</li><li><strong>Borramiento del psoas</strong> → Inflamación retroperitoneal</li><li><strong>Derrame pleural bilateral</strong> → SIRS (Síndrome de Respuesta Inflamatoria Sistémica)</li></ol><h3 id="complicaciones-identificadas" tabindex="-1">Complicaciones Identificadas <a class="header-anchor" href="#complicaciones-identificadas" aria-label="Permalink to &quot;Complicaciones Identificadas&quot;">​</a></h3><ol><li><strong>Hidronefrosis bilateral</strong>: Posible obstrucción ureteral o vejiga neurogénica</li><li><strong>Íleo prolongado</strong>: Disfunción intestinal post-sepsis</li><li><strong>Compromiso pulmonar</strong>: Atelectasias y derrame pleural</li></ol><h3 id="monitoreo-imagenologico-requerido" tabindex="-1">Monitoreo Imagenológico Requerido <a class="header-anchor" href="#monitoreo-imagenologico-requerido" aria-label="Permalink to &quot;Monitoreo Imagenológico Requerido&quot;">​</a></h3><ul><li><strong>Control radiológico seriado</strong>: Evolución del íleo</li><li><strong>Ultrasonido renal</strong>: Seguimiento de hidronefrosis</li><li><strong>Radiografía de tórax</strong>: Evolución del derrame pleural</li></ul><hr><h2 id="interpretacion-integral" tabindex="-1">Interpretación Integral <a class="header-anchor" href="#interpretacion-integral" aria-label="Permalink to &quot;Interpretación Integral&quot;">​</a></h2><h3 id="progresion-del-cuadro-septico-abdominal" tabindex="-1">Progresión del Cuadro Séptico Abdominal <a class="header-anchor" href="#progresion-del-cuadro-septico-abdominal" aria-label="Permalink to &quot;Progresión del Cuadro Séptico Abdominal&quot;">​</a></h3><h4 id="secuencia-de-eventos-radiologicos" tabindex="-1">Secuencia de Eventos Radiológicos <a class="header-anchor" href="#secuencia-de-eventos-radiologicos" aria-label="Permalink to &quot;Secuencia de Eventos Radiológicos&quot;">​</a></h4><ol><li><strong>01.07.25</strong>: Perforación intestinal (aire libre)</li><li><strong>04.07.25</strong>: Complicaciones sistémicas (hidronefrosis, líquido libre)</li><li><strong>06.07.25</strong>: Íleo mecánico pre-exploración</li><li><strong>07.07.25</strong>: Estado postquirúrgico con compromiso sistémico</li></ol><h4 id="impacto-en-sistemas" tabindex="-1">Impacto en Sistemas <a class="header-anchor" href="#impacto-en-sistemas" aria-label="Permalink to &quot;Impacto en Sistemas&quot;">​</a></h4><ul><li><strong>Gastrointestinal</strong>: Perforación → Peritonitis → Íleo</li><li><strong>Renal</strong>: Hidronefrosis bilateral (posible obstrucción)</li><li><strong>Pulmonar</strong>: Derrame pleural bilateral (respuesta sistémica)</li><li><strong>Retroperitoneal</strong>: Inflamación (borramiento del psoas)</li></ul><h3 id="pronostico-imagenologico" tabindex="-1">Pronóstico Imagenológico <a class="header-anchor" href="#pronostico-imagenologico" aria-label="Permalink to &quot;Pronóstico Imagenológico&quot;">​</a></h3><p>🔶 <strong>Proceso complejo</strong> con compromiso multi-sistémico<br> ⚠️ <strong>Requiere seguimiento estricto</strong> de complicaciones<br> 🔍 <strong>Necesidad de estudios de control</strong> para evaluar resolución</p><hr><h2 id="recomendaciones-de-seguimiento" tabindex="-1">Recomendaciones de Seguimiento <a class="header-anchor" href="#recomendaciones-de-seguimiento" aria-label="Permalink to &quot;Recomendaciones de Seguimiento&quot;">​</a></h2><h3 id="estudios-de-control-sugeridos" tabindex="-1">Estudios de Control Sugeridos <a class="header-anchor" href="#estudios-de-control-sugeridos" aria-label="Permalink to &quot;Estudios de Control Sugeridos&quot;">​</a></h3><ol><li><strong>Radiografía de abdomen seriada</strong> (cada 48-72h) para evolución del íleo</li><li><strong>Ultrasonido renal</strong> (semanal) para seguimiento de hidronefrosis</li><li><strong>Radiografía de tórax</strong> (cada 48h) para evolución del derrame pleural</li><li><strong>TAC abdominal con contraste</strong> si hay sospecha de colecciones residuales</li></ol><h3 id="criterios-de-mejoria-radiologica" tabindex="-1">Criterios de Mejoría Radiológica <a class="header-anchor" href="#criterios-de-mejoria-radiologica" aria-label="Permalink to &quot;Criterios de Mejoría Radiológica&quot;">​</a></h3><ul><li>Resolución gradual del íleo (disminución de niveles hidroaéreos)</li><li>Normalización del patrón gaseoso intestinal</li><li>Disminución del derrame pleural</li><li>Mejora de la hidronefrosis bilateral</li></ul><hr><p><em>Última actualización: 16.07.2025</em><br><em>Reporte imagenológico integral - Evolución 01.07 al 07.07.2025</em></p>',94)]))}const h=o(n,[["render",l]]);export{m as __pageData,h as default};
