import{_ as e,c as o,o as i,ag as r}from"./chunks/framework.Bw-5EFTY.js";const m=JSON.parse('{"title":"Protocolos de Cuidados de Soporte","description":"","frontmatter":{},"headers":[],"relativePath":"modulos-clinicos/04-tratamientos/cuidados-soporte.md","filePath":"modulos-clinicos/04-tratamientos/cuidados-soporte.md"}'),n={name:"modulos-clinicos/04-tratamientos/cuidados-soporte.md"};function l(t,a,s,d,c,u){return i(),o("div",null,a[0]||(a[0]=[r('<h1 id="protocolos-de-cuidados-de-soporte" tabindex="-1">Protocolos de Cuidados de Soporte <a class="header-anchor" href="#protocolos-de-cuidados-de-soporte" aria-label="Permalink to &quot;Protocolos de Cuidados de Soporte&quot;">​</a></h1><h2 id="resumen-del-paciente" tabindex="-1">Resumen del Paciente <a class="header-anchor" href="#resumen-del-paciente" aria-label="Permalink to &quot;Resumen del Paciente&quot;">​</a></h2><p><strong>Paciente:</strong> Dylan Antonio Martinez Labastida<br><strong>Edad:</strong> 8 años<br><strong>Peso:</strong> 37.70kg → 33.70kg (fase crítica)</p><hr><h2 id="soporte-respiratorio" tabindex="-1">Soporte Respiratorio <a class="header-anchor" href="#soporte-respiratorio" aria-label="Permalink to &quot;Soporte Respiratorio&quot;">​</a></h2><h3 id="fase-i-pre-intubacion" tabindex="-1">Fase I: Pre-Intubación <a class="header-anchor" href="#fase-i-pre-intubacion" aria-label="Permalink to &quot;Fase I: Pre-Intubación&quot;">​</a></h3><p><strong>Estado:</strong> Sin deterioro ventilatorio</p><ul><li>Paciente estable respiratoriamente al ingreso</li><li>Campos pulmonares ventilados bilateralmente</li></ul><h3 id="fase-ii-intubacion-intraoperatoria-6-de-julio-2025" tabindex="-1">Fase II: Intubación Intraoperatoria (6 de julio, 2025) <a class="header-anchor" href="#fase-ii-intubacion-intraoperatoria-6-de-julio-2025" aria-label="Permalink to &quot;Fase II: Intubación Intraoperatoria (6 de julio, 2025)&quot;">​</a></h3><p><strong>Detalles de Intubación:</strong></p><ul><li><strong>Cánula:</strong> 5.5 FR con globo</li><li><strong>Posición:</strong> Fija en 16.5 cm de arcada dental</li><li><strong>Técnica:</strong> Al primer intento (anestesiología)</li><li><strong>Indicación:</strong> Cirugía mayor + inestabilidad hemodinámica</li></ul><h3 id="fase-iii-ventilacion-mecanica-fase-intensiva" tabindex="-1">Fase III: Ventilación Mecánica - Fase Intensiva <a class="header-anchor" href="#fase-iii-ventilacion-mecanica-fase-intensiva" aria-label="Permalink to &quot;Fase III: Ventilación Mecánica - Fase Intensiva&quot;">​</a></h3><p><strong>Configuración del Ventilador (6-7 de julio, 2025):</strong></p><ul><li><strong>Modo:</strong> SIMV (Ventilación Mandatoria Intermitente Sincronizada)</li><li><strong>FiO2:</strong> 35% → 45% → 35% (ajuste por hiperoxemia)</li><li><strong>Volumen tidal:</strong> 7.4ml/kg → 5ml/kg</li><li><strong>PEEP:</strong> 5 cmH2O → 6 cmH2O</li><li><strong>Frecuencia respiratoria:</strong> 18 rpm</li><li><strong>I:E:</strong> 1:2.3</li><li><strong>Oximetría:</strong> &gt;94% mantenida</li></ul><p><strong>Monitoreo:</strong></p><ul><li>Gasometrías arteriales frecuentes</li><li>Ajuste de FiO2 por hiperoxemia</li><li>Manejo dinámico del ventilador</li></ul><h3 id="fase-iii-ventilacion-mecanica-controlada-8-12-de-julio-2025" tabindex="-1">Fase III: Ventilación Mecánica Controlada (8-12 de julio, 2025) <a class="header-anchor" href="#fase-iii-ventilacion-mecanica-controlada-8-12-de-julio-2025" aria-label="Permalink to &quot;Fase III: Ventilación Mecánica Controlada (8-12 de julio, 2025)&quot;">​</a></h3><p><strong>Configuración del Ventilador:</strong></p><ul><li><strong>Modo:</strong> CMV (Ventilación Mecánica Controlada) → Controlado por volumen</li><li><strong>FiO2:</strong> 60% → 90% (según requerimientos)</li><li><strong>Volumen tidal:</strong> 5.5ml/kg</li><li><strong>PEEP:</strong> 6 cmH2O</li><li><strong>Limitado por flujo</strong></li></ul><p><strong>Hallazgos Clínicos:</strong></p><ul><li>Campos pulmonares bien ventilados</li><li>Murmullo vesicular presente bilateralmente</li><li>Sin estertores ni sibilancias</li><li>Sin síndrome pleuropulmonar inicial</li></ul><h3 id="fase-iv-preparacion-para-destete-13-de-julio-2025" tabindex="-1">Fase IV: Preparación para Destete (13 de julio, 2025) <a class="header-anchor" href="#fase-iv-preparacion-para-destete-13-de-julio-2025" aria-label="Permalink to &quot;Fase IV: Preparación para Destete (13 de julio, 2025)&quot;">​</a></h3><p><strong>Transición del Ventilador:</strong></p><ul><li><strong>Modo:</strong> Modalidad espontánea (preparación para extubación)</li><li>Pruebas de ventilación espontánea</li><li>Evaluación de drive respiratorio</li></ul><h3 id="fase-v-extubacion-exitosa-13-de-julio-2025" tabindex="-1">Fase V: Extubación Exitosa (13 de julio, 2025) <a class="header-anchor" href="#fase-v-extubacion-exitosa-13-de-julio-2025" aria-label="Permalink to &quot;Fase V: Extubación Exitosa (13 de julio, 2025)&quot;">​</a></h3><p><strong>Proceso de Extubación:</strong></p><ul><li><strong>Extubación programada exitosa</strong></li><li><strong>Progresión:</strong> Fase I con mascarilla reservorio</li><li><strong>Complicaciones menores:</strong><ul><li>Estridor transitorio post-extubación</li><li>Sibilancias espiratorias transitorias</li><li>Remisión espontánea</li></ul></li></ul><p><strong>Soporte Post-Extubación:</strong></p><ul><li>Oxigenoterapia con mascarilla reservorio</li><li>Monitoreo estrecho de saturación</li><li>Vigilancia de signos de falla respiratoria</li></ul><h3 id="fase-vi-independencia-respiratoria-14-de-julio-2025" tabindex="-1">Fase VI: Independencia Respiratoria (14 de julio, 2025) <a class="header-anchor" href="#fase-vi-independencia-respiratoria-14-de-julio-2025" aria-label="Permalink to &quot;Fase VI: Independencia Respiratoria (14 de julio, 2025)&quot;">​</a></h3><p><strong>Estado Actual:</strong></p><ul><li>Tolerando extubación adecuadamente</li><li>Campos pulmonares con buena entrada y salida de aire</li><li>Murmullo vesicular presente aunque disminuido</li><li><strong>Complicación:</strong> Derrame pleural bilateral (manejado con furosemida)</li></ul><hr><h2 id="soporte-hemodinamico" tabindex="-1">Soporte Hemodinámico <a class="header-anchor" href="#soporte-hemodinamico" aria-label="Permalink to &quot;Soporte Hemodinámico&quot;">​</a></h2><h3 id="estabilidad-pre-operatoria" tabindex="-1">Estabilidad Pre-Operatoria <a class="header-anchor" href="#estabilidad-pre-operatoria" aria-label="Permalink to &quot;Estabilidad Pre-Operatoria&quot;">​</a></h3><p><strong>Estado:</strong> Hemodinámicamente estable al ingreso</p><ul><li>Signos vitales dentro de percentiles para edad</li><li>Sin datos de bajo gasto cardíaco</li></ul><h3 id="crisis-hemodinamica-intraoperatoria-6-de-julio-2025" tabindex="-1">Crisis Hemodinámica Intraoperatoria (6 de julio, 2025) <a class="header-anchor" href="#crisis-hemodinamica-intraoperatoria-6-de-julio-2025" aria-label="Permalink to &quot;Crisis Hemodinámica Intraoperatoria (6 de julio, 2025)&quot;">​</a></h3><p><strong>Manejo de Crisis:</strong></p><ul><li><strong>Desencadenante:</strong> Cifras tensionales por debajo de percentil durante cirugía</li><li><strong>Respuesta Inicial:</strong> Carga con solución Hartmann 10ml/kg (sin respuesta)</li><li><strong>Soporte Vasopresor:</strong><ul><li>Norepinefrina: Máximo 1mcg/kg/min intraoperatorio</li><li>Posterior descenso a 0.1mcg/kg/min post-operatorio</li></ul></li></ul><h3 id="manejo-hemodinamico-en-uci-6-14-de-julio-2025" tabindex="-1">Manejo Hemodinámico en UCI (6-14 de julio, 2025) <a class="header-anchor" href="#manejo-hemodinamico-en-uci-6-14-de-julio-2025" aria-label="Permalink to &quot;Manejo Hemodinámico en UCI (6-14 de julio, 2025)&quot;">​</a></h3><h4 id="fase-1-soporte-agudo-6-7-de-julio-2025" tabindex="-1"><strong>Fase 1: Soporte Agudo (6-7 de julio, 2025)</strong> <a class="header-anchor" href="#fase-1-soporte-agudo-6-7-de-julio-2025" aria-label="Permalink to &quot;**Fase 1: Soporte Agudo (6-7 de julio, 2025)**&quot;">​</a></h4><p><strong>Protocolo Vasopresor:</strong></p><ul><li><strong>Noradrenalina:</strong> 0.3mcg/kg/min</li><li><strong>Fundamento:</strong> Tensiones arteriales sobre percentil</li><li><strong>Ajuste:</strong> Descenso progresivo a 0.1mcg/kg/min</li><li><strong>Monitoreo:</strong> Frecuencias cardíacas y tensiones arteriales dentro de percentiles</li></ul><p><strong>Evaluación Clínica:</strong></p><ul><li>Sin datos de bajo gasto cardíaco</li><li>Precordio con ruidos cardíacos rítmicos de buen tono e intensidad</li><li>No se auscultan soplos</li><li>Pulsos periféricos presentes y de adecuada intensidad</li><li>Llenado capilar: 2 segundos</li></ul><h4 id="fase-2-transicion-a-epinefrina-7-12-de-julio-2025" tabindex="-1"><strong>Fase 2: Transición a Epinefrina (7-12 de julio, 2025)</strong> <a class="header-anchor" href="#fase-2-transicion-a-epinefrina-7-12-de-julio-2025" aria-label="Permalink to &quot;**Fase 2: Transición a Epinefrina (7-12 de julio, 2025)**&quot;">​</a></h4><p><strong>Transición de Medicación:</strong></p><ul><li><strong>Adrenalina:</strong> Cambio de noradrenalina</li><li><strong>Evolución de Dosis:</strong><ul><li>0.5mcg/kg/min → 0.04mcg/kg/min → 0.03mcg/kg/min</li></ul></li><li><strong>Respuesta Clínica:</strong> Estabilización progresiva</li></ul><p><strong>Monitoreo Hemodinámico:</strong></p><ul><li>Precordio normodinámico, normofonético</li><li>Pulsos centrales y periféricos presentes</li><li>Llenado capilar: 2 segundos mantenido</li><li>Balance hídrico negativo con uresis conservada</li></ul><h4 id="fase-3-destete-12-14-de-julio-2025" tabindex="-1"><strong>Fase 3: Destete (12-14 de julio, 2025)</strong> <a class="header-anchor" href="#fase-3-destete-12-14-de-julio-2025" aria-label="Permalink to &quot;**Fase 3: Destete (12-14 de julio, 2025)**&quot;">​</a></h4><p><strong>Destete Exitoso:</strong></p><ul><li>Suspensión progresiva de soporte vasopresor</li><li>Mantenimiento de estabilidad hemodinámica</li><li>Sin datos de bajo gasto cardíaco</li><li>Sin respuesta inflamatoria sistémica</li></ul><hr><h2 id="manejo-de-liquidos-y-electrolitos" tabindex="-1">Manejo de Líquidos y Electrolitos <a class="header-anchor" href="#manejo-de-liquidos-y-electrolitos" aria-label="Permalink to &quot;Manejo de Líquidos y Electrolitos&quot;">​</a></h2><h3 id="manejo-de-liquidos-fase-aguda-6-8-de-julio-2025" tabindex="-1">Manejo de Líquidos Fase Aguda (6-8 de julio, 2025) <a class="header-anchor" href="#manejo-de-liquidos-fase-aguda-6-8-de-julio-2025" aria-label="Permalink to &quot;Manejo de Líquidos Fase Aguda (6-8 de julio, 2025)&quot;">​</a></h3><h4 id="requerimientos-iniciales-de-liquidos" tabindex="-1"><strong>Requerimientos Iniciales de Líquidos:</strong> <a class="header-anchor" href="#requerimientos-iniciales-de-liquidos" aria-label="Permalink to &quot;**Requerimientos Iniciales de Líquidos:**&quot;">​</a></h4><ul><li><strong>Líquidos IV totales:</strong> 2000ml/m²SC/día → 1800ml/m²SC/día</li><li><strong>Esquema de soluciones:</strong> 860/50/40 → 1000/50/40/100/50 GKM 1.4</li></ul><h4 id="correcciones-electroliticas" tabindex="-1"><strong>Correcciones Electrolíticas:</strong> <a class="header-anchor" href="#correcciones-electroliticas" aria-label="Permalink to &quot;**Correcciones Electrolíticas:**&quot;">​</a></h4><ul><li><strong>Hipokalemia:</strong> Aporte total 60mEq/m²SC/día</li><li><strong>Hipoalbuminemia:</strong> Albúmina 1gr/kg/dosis cada 24 horas (nivel inicial 1.9)</li></ul><h3 id="fase-de-optimizacion-de-liquidos-8-12-de-julio-2025" tabindex="-1">Fase de Optimización de Líquidos (8-12 de julio, 2025) <a class="header-anchor" href="#fase-de-optimizacion-de-liquidos-8-12-de-julio-2025" aria-label="Permalink to &quot;Fase de Optimización de Líquidos (8-12 de julio, 2025)&quot;">​</a></h3><h4 id="protocolo-diuretico" tabindex="-1"><strong>Protocolo Diurético:</strong> <a class="header-anchor" href="#protocolo-diuretico" aria-label="Permalink to &quot;**Protocolo Diurético:**&quot;">​</a></h4><ul><li><strong>Furosemida:</strong> 0.5mg/kg/dosis → infusión continua → 10mg cada 8 horas</li><li><strong>Indicación:</strong> Sobrecarga hídrica, edema en extremidades inferiores y genital</li><li><strong>Monitoreo:</strong> Volúmenes urinarios, balance hídrico</li></ul><h4 id="manejo-de-balance" tabindex="-1"><strong>Manejo de Balance:</strong> <a class="header-anchor" href="#manejo-de-balance" aria-label="Permalink to &quot;**Manejo de Balance:**&quot;">​</a></h4><ul><li><strong>Uresis:</strong> 2.1ml/kg/24h → 1.2ml/kg/hr media</li><li><strong>Balance hídrico:</strong> Progresión a balance negativo (-118ml)</li><li><strong>Proyección de líquidos:</strong> 1512ml/SC/día</li></ul><h3 id="fase-de-recuperacion-13-14-de-julio-2025" tabindex="-1">Fase de Recuperación (13-14 de julio, 2025) <a class="header-anchor" href="#fase-de-recuperacion-13-14-de-julio-2025" aria-label="Permalink to &quot;Fase de Recuperación (13-14 de julio, 2025)&quot;">​</a></h3><h4 id="normalizacion-de-liquidos" tabindex="-1"><strong>Normalización de Líquidos:</strong> <a class="header-anchor" href="#normalizacion-de-liquidos" aria-label="Permalink to &quot;**Normalización de Líquidos:**&quot;">​</a></h4><ul><li>Suspensión de diurético con horario</li><li>Uresis espontánea adecuada</li><li>Resolución de edemas</li><li><strong>Complicación:</strong> Derrame pleural bilateral (tratado con furosemida PRN)</li></ul><hr><h2 id="soporte-nutricional" tabindex="-1">Soporte Nutricional <a class="header-anchor" href="#soporte-nutricional" aria-label="Permalink to &quot;Soporte Nutricional&quot;">​</a></h2><h3 id="fase-aguda-reposo-intestinal-completo-6-12-de-julio-2025" tabindex="-1">Fase Aguda: Reposo Intestinal Completo (6-12 de julio, 2025) <a class="header-anchor" href="#fase-aguda-reposo-intestinal-completo-6-12-de-julio-2025" aria-label="Permalink to &quot;Fase Aguda: Reposo Intestinal Completo (6-12 de julio, 2025)&quot;">​</a></h3><p><strong>Estado Nutricional:</strong></p><ul><li><strong>Ayuno completo</strong> por abdomen abierto y fístula enterocutánea</li><li><strong>Sonda nasogástrica:</strong> Derivación con gasto biliar moderado</li><li><strong>Esquema de soluciones:</strong> Soporte hidroelectrolítico básico</li></ul><h3 id="implementacion-de-soporte-metabolico-8-12-de-julio-2025" tabindex="-1">Implementación de Soporte Metabólico (8-12 de julio, 2025) <a class="header-anchor" href="#implementacion-de-soporte-metabolico-8-12-de-julio-2025" aria-label="Permalink to &quot;Implementación de Soporte Metabólico (8-12 de julio, 2025)&quot;">​</a></h3><p><strong>Nutrición Parenteral:</strong></p><ul><li><strong>Nutrición parenteral total (NPT):</strong> Basal</li><li><strong>Soporte Proteico:</strong> Albúmina 1gr/kg/día por hipoalbuminemia</li><li><strong>Monitoreo:</strong> Parámetros basales, equilibrio metabólico</li></ul><h3 id="fase-de-estimulacion-enteral-14-de-julio-2025" tabindex="-1">Fase de Estimulación Enteral (14 de julio, 2025) <a class="header-anchor" href="#fase-de-estimulacion-enteral-14-de-julio-2025" aria-label="Permalink to &quot;Fase de Estimulación Enteral (14 de julio, 2025)&quot;">​</a></h3><p><strong>Transición a Nutrición Enteral:</strong></p><ul><li><strong>Inicio de estímulo enteral</strong> con dieta líquida</li><li><strong>Progresión:</strong> Gradual según tolerancia</li><li><strong>Monitoreo:</strong> Tolerancia gastrointestinal, gasto por ileostomía</li></ul><hr><h2 id="soporte-gastrointestinal" tabindex="-1">Soporte Gastrointestinal <a class="header-anchor" href="#soporte-gastrointestinal" aria-label="Permalink to &quot;Soporte Gastrointestinal&quot;">​</a></h2><h3 id="proteccion-gastrica" tabindex="-1">Protección Gástrica <a class="header-anchor" href="#proteccion-gastrica" aria-label="Permalink to &quot;Protección Gástrica&quot;">​</a></h3><p><strong>Protocolo de Inhibidor de Bomba de Protones:</strong></p><ul><li><strong>Omeprazol:</strong> 1mg/kg/dosis</li><li><strong>Duración:</strong> Continuo durante toda la estancia</li><li><strong>Indicación:</strong> Prevención de úlcera por estrés en paciente crítico</li></ul><h3 id="descompresion-abdominal" tabindex="-1">Descompresión Abdominal <a class="header-anchor" href="#descompresion-abdominal" aria-label="Permalink to &quot;Descompresión Abdominal&quot;">​</a></h3><p><strong>Drenaje Nasogástrico:</strong></p><ul><li><strong>Sonda nasogástrica:</strong> Derivación continua</li><li><strong>Gasto:</strong> Gasto biliar en moderada cantidad</li><li><strong>Monitoreo:</strong> Volumen y características del gasto</li></ul><h3 id="manejo-intestinal" tabindex="-1">Manejo Intestinal <a class="header-anchor" href="#manejo-intestinal" aria-label="Permalink to &quot;Manejo Intestinal&quot;">​</a></h3><p><strong>Cuidados de Ileostomía:</strong></p><ul><li><strong>Ileostomía:</strong> Bordes rosados, sin compromiso vascular</li><li><strong>Función:</strong> Gasto controlado</li><li><strong>Protección de Piel:</strong> Cuidados especializados de estoma</li></ul><hr><h2 id="soporte-y-monitoreo-renal" tabindex="-1">Soporte y Monitoreo Renal <a class="header-anchor" href="#soporte-y-monitoreo-renal" aria-label="Permalink to &quot;Soporte y Monitoreo Renal&quot;">​</a></h2><h3 id="evaluacion-de-funcion-renal" tabindex="-1">Evaluación de Función Renal <a class="header-anchor" href="#evaluacion-de-funcion-renal" aria-label="Permalink to &quot;Evaluación de Función Renal&quot;">​</a></h3><p><strong>Función Basal:</strong></p><ul><li>Sin datos de lesión renal aguda por bioquímicos</li><li><strong>Uresis:</strong> 2.1ml/kg/24h inicialmente</li><li><strong>Creatinina:</strong> Dentro de límites normales</li></ul><h3 id="manejo-de-balance-hidrico" tabindex="-1">Manejo de Balance Hídrico <a class="header-anchor" href="#manejo-de-balance-hidrico" aria-label="Permalink to &quot;Manejo de Balance Hídrico&quot;">​</a></h3><p><strong>Soporte Diurético:</strong></p><ul><li><strong>Furosemida:</strong> Forzamiento diurético controlado</li><li><strong>Albúmina:</strong> Reposición simultánea para mantener presión oncótica</li><li><strong>Monitoreo:</strong> Balance hídrico estricto</li></ul><h3 id="manejo-de-edema" tabindex="-1">Manejo de Edema <a class="header-anchor" href="#manejo-de-edema" aria-label="Permalink to &quot;Manejo de Edema&quot;">​</a></h3><p><strong>Hallazgos Clínicos:</strong></p><ul><li><strong>Edema:</strong> + en extremidades inferiores y genital (fase aguda)</li><li><strong>Resolución:</strong> Progresiva con manejo diurético</li><li><strong>Llenado capilar:</strong> Mantenido en 2 segundos</li></ul><hr><h2 id="protocolos-generales-de-cuidados-en-uci" tabindex="-1">Protocolos Generales de Cuidados en UCI <a class="header-anchor" href="#protocolos-generales-de-cuidados-en-uci" aria-label="Permalink to &quot;Protocolos Generales de Cuidados en UCI&quot;">​</a></h2><h3 id="prevencion-de-infecciones" tabindex="-1">Prevención de Infecciones <a class="header-anchor" href="#prevencion-de-infecciones" aria-label="Permalink to &quot;Prevención de Infecciones&quot;">​</a></h3><p><strong>Controles Ambientales:</strong></p><ul><li>Cuidados generales de enfermería</li><li>Cuidados específicos de sondas y catéteres</li><li>Asepsia estricta en procedimientos</li></ul><h3 id="protocolos-de-monitoreo" tabindex="-1">Protocolos de Monitoreo <a class="header-anchor" href="#protocolos-de-monitoreo" aria-label="Permalink to &quot;Protocolos de Monitoreo&quot;">​</a></h3><p><strong>Signos Vitales:</strong></p><ul><li>Signos vitales cada hora</li><li>Vigilancia de curva térmica</li><li>Monitoreo cardiorrespiratorio continuo</li></ul><h3 id="cuidados-especializados" tabindex="-1">Cuidados Especializados <a class="header-anchor" href="#cuidados-especializados" aria-label="Permalink to &quot;Cuidados Especializados&quot;">​</a></h3><p><strong>Estándares de Cuidados Críticos:</strong></p><ul><li>Escala de sedación (RASS)</li><li>Prevención de úlceras por presión</li><li>Cuidados de la vía aérea</li><li>Manejo del abdomen abierto</li></ul><hr><h2 id="estado-actual-de-soporte-14-de-julio-2025" tabindex="-1">Estado Actual de Soporte (14 de julio, 2025) <a class="header-anchor" href="#estado-actual-de-soporte-14-de-julio-2025" aria-label="Permalink to &quot;Estado Actual de Soporte (14 de julio, 2025)&quot;">​</a></h2><h3 id="respiratorio" tabindex="-1"><strong>Respiratorio:</strong> <a class="header-anchor" href="#respiratorio" aria-label="Permalink to &quot;**Respiratorio:**&quot;">​</a></h3><ul><li>Extubado, tolerando aire ambiente</li><li>Vigilancia de derrame pleural bilateral</li></ul><h3 id="hemodinamico" tabindex="-1"><strong>Hemodinámico:</strong> <a class="header-anchor" href="#hemodinamico" aria-label="Permalink to &quot;**Hemodinámico:**&quot;">​</a></h3><ul><li>Estable sin soporte vasopresor</li><li>Hemodinámicamente compensado</li></ul><h3 id="nutricional" tabindex="-1"><strong>Nutricional:</strong> <a class="header-anchor" href="#nutricional" aria-label="Permalink to &quot;**Nutricional:**&quot;">​</a></h3><ul><li>Iniciando estímulo enteral</li><li>NPT de soporte</li></ul><h3 id="renal" tabindex="-1"><strong>Renal:</strong> <a class="header-anchor" href="#renal" aria-label="Permalink to &quot;**Renal:**&quot;">​</a></h3><ul><li>Función renal preservada</li><li>Diuréticos PRN para derrame pleural</li></ul><hr><h2 id="referencias-cruzadas" tabindex="-1">Referencias Cruzadas <a class="header-anchor" href="#referencias-cruzadas" aria-label="Permalink to &quot;Referencias Cruzadas&quot;">​</a></h2><ul><li><strong>Medicamentos:</strong> <a href="./protocolos-medicacion.html">Protocolos de Medicación</a></li><li><strong>Cirugía:</strong> <a href="./procedimientos-quirurgicos.html">Procedimientos Quirúrgicos</a></li><li><strong>Monitoreo:</strong> <a href="./../03-diagnosticos/">Diagnósticos</a></li><li><strong>Evaluación:</strong> <a href="./../05-evaluaciones/">Evaluación Clínica</a></li></ul>',126)]))}const p=e(n,[["render",l]]);export{m as __pageData,p as default};
