import{_ as a,c as o,o as e,ag as r}from"./chunks/framework.Bw-5EFTY.js";const h=JSON.parse('{"title":"Procedimientos Quirúrgicos","description":"","frontmatter":{},"headers":[],"relativePath":"modulos-clinicos/04-tratamientos/procedimientos-quirurgicos.md","filePath":"modulos-clinicos/04-tratamientos/procedimientos-quirurgicos.md"}'),n={name:"modulos-clinicos/04-tratamientos/procedimientos-quirurgicos.md"};function t(s,i,l,c,u,d){return e(),o("div",null,i[0]||(i[0]=[r('<h1 id="procedimientos-quirurgicos" tabindex="-1">Procedimientos Quirúrgicos <a class="header-anchor" href="#procedimientos-quirurgicos" aria-label="Permalink to &quot;Procedimientos Quirúrgicos&quot;">​</a></h1><h2 id="resumen-del-paciente" tabindex="-1">Resumen del Paciente <a class="header-anchor" href="#resumen-del-paciente" aria-label="Permalink to &quot;Resumen del Paciente&quot;">​</a></h2><p><strong>Paciente:</strong> Dylan Antonio Martinez Labastida<br><strong>Edad:</strong> 8 años<br><strong>Peso:</strong> 37.70kg (33.70kg durante fase crítica)</p><hr><h2 id="cronologia-quirurgica" tabindex="-1">Cronología Quirúrgica <a class="header-anchor" href="#cronologia-quirurgica" aria-label="Permalink to &quot;Cronología Quirúrgica&quot;">​</a></h2><h3 id="_1-cirugia-inicial-institucion-externa-clinica-santa-maria-xicotepec" tabindex="-1">1. Cirugía Inicial - Institución Externa (Clínica Santa María, Xicotepec) <a class="header-anchor" href="#_1-cirugia-inicial-institucion-externa-clinica-santa-maria-xicotepec" aria-label="Permalink to &quot;1. Cirugía Inicial - Institución Externa (Clínica Santa María, Xicotepec)&quot;">​</a></h3><p><strong>Fecha:</strong> 24 de junio, 2025<br><strong>Procedimiento:</strong> Laparotomía exploradora + Apendicectomía</p><h4 id="hallazgos" tabindex="-1"><strong>Hallazgos:</strong> <a class="header-anchor" href="#hallazgos" aria-label="Permalink to &quot;**Hallazgos:**&quot;">​</a></h4><ul><li>Apendicitis aguda complicada Fase IV</li><li>Pelviperitonitis</li><li>Material purulento libre aproximadamente 50ml, fétido</li><li>Lisis de apéndice cecal</li><li>Coprolito libre</li><li>Ciego friable</li></ul><h4 id="tecnica-quirurgica" tabindex="-1"><strong>Técnica Quirúrgica:</strong> <a class="header-anchor" href="#tecnica-quirurgica" aria-label="Permalink to &quot;**Técnica Quirúrgica:**&quot;">​</a></h4><ul><li>Apendicectomía completa</li><li>Drenaje tipo Penrose colocado</li><li>Lavado de cavidad peritoneal</li></ul><h4 id="complicaciones" tabindex="-1"><strong>Complicaciones:</strong> <a class="header-anchor" href="#complicaciones" aria-label="Permalink to &quot;**Complicaciones:**&quot;">​</a></h4><ul><li><strong>1 de julio, 2025:</strong> Fístula enterocutánea con gasto fecaloide por drenaje Penrose y herida quirúrgica</li><li>Dehiscencia de herida quirúrgica (se retira un punto para curación)</li></ul><hr><h3 id="_2-cirugia-reconstructiva-institucion-actual" tabindex="-1">2. Cirugía Reconstructiva - Institución Actual <a class="header-anchor" href="#_2-cirugia-reconstructiva-institucion-actual" aria-label="Permalink to &quot;2. Cirugía Reconstructiva - Institución Actual&quot;">​</a></h3><p><strong>Fecha:</strong> 6 de julio, 2025<br><strong>Procedimiento:</strong> Laparotomía exploradora + Reparación de perforación + Cierre de muñón</p><h4 id="estado-pre-operatorio" tabindex="-1"><strong>Estado Pre-operatorio:</strong> <a class="header-anchor" href="#estado-pre-operatorio" aria-label="Permalink to &quot;**Estado Pre-operatorio:**&quot;">​</a></h4><ul><li>Paciente altamente complicado por tiempo de evolución</li><li>Fístula enterocutánea activa</li><li>Sepsis abdominal probable</li></ul><h4 id="hallazgos-quirurgicos" tabindex="-1"><strong>Hallazgos Quirúrgicos:</strong> <a class="header-anchor" href="#hallazgos-quirurgicos" aria-label="Permalink to &quot;**Hallazgos Quirúrgicos:**&quot;">​</a></h4><ul><li><strong>Contenido fecaloide:</strong> 300ml</li><li><strong>Abdomen hostil:</strong> Bjork 3B</li><li><strong>Absceso subhepático:</strong> 50ml de pus</li><li><strong>Absceso subesplénico:</strong> 50ml de pus</li><li><strong>Absceso en fosa ilíaca derecha:</strong> 100ml</li><li><strong>Absceso en hueco pélvico:</strong> 100ml</li><li><strong>Muñón apendicular dehiscente</strong></li><li><strong>Ciego en malas condiciones</strong></li><li><strong>Perforación en sigmoides:</strong> 5cm</li></ul><h4 id="tecnica-quirurgica-1" tabindex="-1"><strong>Técnica Quirúrgica:</strong> <a class="header-anchor" href="#tecnica-quirurgica-1" aria-label="Permalink to &quot;**Técnica Quirúrgica:**&quot;">​</a></h4><ul><li><strong>Cierre de muñón:</strong> Punto de Halsted con Vicryl 3-0</li><li><strong>Reparación primaria de sigmoides:</strong> 1 plano con puntos totales de Vicryl 3-0</li><li><strong>Lavado exhaustivo:</strong> Drenaje de todos los abscesos</li><li><strong>Abdomen abierto:</strong> Técnica de abdomen abierto para control de daños</li></ul><h4 id="manejo-anestesico" tabindex="-1"><strong>Manejo Anestésico:</strong> <a class="header-anchor" href="#manejo-anestesico" aria-label="Permalink to &quot;**Manejo Anestésico:**&quot;">​</a></h4><ul><li>Intubación: Cánula 5.5 FR con globo fija en 16.5 cm de arcada dental</li><li>Sangrado intraoperatorio: 300cc</li><li>Transfusión: 1 concentrado eritrocitario</li><li>Soporte vasopresor: Norepinefrina hasta 1mcg/kg/min</li></ul><hr><h3 id="_3-cirugia-de-segunda-revision" tabindex="-1">3. Cirugía de Segunda Revisión <a class="header-anchor" href="#_3-cirugia-de-segunda-revision" aria-label="Permalink to &quot;3. Cirugía de Segunda Revisión&quot;">​</a></h3><p><strong>Fecha:</strong> 8 de julio, 2025<br><strong>Procedimiento:</strong> Laparotomía de revisión + Reparación de dehiscencia</p><h4 id="hallazgos-1" tabindex="-1"><strong>Hallazgos:</strong> <a class="header-anchor" href="#hallazgos-1" aria-label="Permalink to &quot;**Hallazgos:**&quot;">​</a></h4><ul><li>Dehiscencia de reparación previa de sigmoides</li><li>Necesidad de nueva reparación</li></ul><h4 id="accion-quirurgica" tabindex="-1"><strong>Acción Quirúrgica:</strong> <a class="header-anchor" href="#accion-quirurgica" aria-label="Permalink to &quot;**Acción Quirúrgica:**&quot;">​</a></h4><ul><li>Re-reparación de sigmoides</li><li>Continúa abdomen abierto</li><li>Sistema de aspiración artesanal</li></ul><hr><h3 id="_4-cirugia-reconstructiva-con-ostomia" tabindex="-1">4. Cirugía Reconstructiva con Ostomía <a class="header-anchor" href="#_4-cirugia-reconstructiva-con-ostomia" aria-label="Permalink to &quot;4. Cirugía Reconstructiva con Ostomía&quot;">​</a></h3><p><strong>Fecha:</strong> 9 de julio, 2025<br><strong>Procedimiento:</strong> Cierre de pared + Ileostomía</p><h4 id="detalles-quirurgicos" tabindex="-1"><strong>Detalles Quirúrgicos:</strong> <a class="header-anchor" href="#detalles-quirurgicos" aria-label="Permalink to &quot;**Detalles Quirúrgicos:**&quot;">​</a></h4><ul><li><p><strong>Primera fase quirúrgica:</strong></p><ul><li>Introducción de guía metálica hasta obtener extrasístoles</li><li>Procedimiento de acceso vascular</li></ul></li><li><p><strong>Segunda fase quirúrgica:</strong></p><ul><li>Creación de ileostomía terminal</li><li>Cierre de pared abdominal</li><li>Sistema de drenaje por contrabertura izquierda</li><li>Fijación a piel con Prolene 3-0</li></ul></li></ul><h4 id="estado-post-operatorio" tabindex="-1"><strong>Estado Post-operatorio:</strong> <a class="header-anchor" href="#estado-post-operatorio" aria-label="Permalink to &quot;**Estado Post-operatorio:**&quot;">​</a></h4><ul><li>Ileostomía con bordes rosados</li><li>Sin datos de compromiso vascular</li><li>Herida quirúrgica sin dehiscencia o secreciones</li><li>Abdomen blando, depresible</li><li>Peristalsis disminuida</li></ul><hr><h2 id="resumen-de-complicaciones-quirurgicas" tabindex="-1">Resumen de Complicaciones Quirúrgicas <a class="header-anchor" href="#resumen-de-complicaciones-quirurgicas" aria-label="Permalink to &quot;Resumen de Complicaciones Quirúrgicas&quot;">​</a></h2><h3 id="complicaciones-primarias" tabindex="-1"><strong>Complicaciones Primarias:</strong> <a class="header-anchor" href="#complicaciones-primarias" aria-label="Permalink to &quot;**Complicaciones Primarias:**&quot;">​</a></h3><ol><li><strong>Apendicitis complicada Fase IV</strong> - Inicial</li><li><strong>Fístula enterocutánea</strong> - 1 de julio, 2025</li><li><strong>Dehiscencia de herida quirúrgica</strong> - 1 de julio, 2025</li><li><strong>Sepsis abdominal</strong> - 5-6 de julio, 2025</li><li><strong>Dehiscencia de reparación intestinal</strong> - 8 de julio, 2025</li></ol><h3 id="estrategia-de-manejo-quirurgico" tabindex="-1"><strong>Estrategia de Manejo Quirúrgico:</strong> <a class="header-anchor" href="#estrategia-de-manejo-quirurgico" aria-label="Permalink to &quot;**Estrategia de Manejo Quirúrgico:**&quot;">​</a></h3><ul><li><strong>Control de daños:</strong> Abdomen abierto prolongado</li><li><strong>Drenaje:</strong> Múltiples abscesos intraabdominales</li><li><strong>Derivación:</strong> Ileostomía terminal para protección</li><li><strong>Reconstrucción diferida:</strong> Enfoque por etapas</li></ul><hr><h2 id="pronostico-y-seguimiento" tabindex="-1">Pronóstico y Seguimiento <a class="header-anchor" href="#pronostico-y-seguimiento" aria-label="Permalink to &quot;Pronóstico y Seguimiento&quot;">​</a></h2><h3 id="estado-actual" tabindex="-1"><strong>Estado Actual:</strong> <a class="header-anchor" href="#estado-actual" aria-label="Permalink to &quot;**Estado Actual:**&quot;">​</a></h3><ul><li>Ileostomía funcionante</li><li>Herida quirúrgica estable</li><li>Resolución gradual de sepsis</li><li>Tolerancia al destete ventilatorio</li></ul><h3 id="planes-quirurgicos-futuros" tabindex="-1"><strong>Planes Quirúrgicos Futuros:</strong> <a class="header-anchor" href="#planes-quirurgicos-futuros" aria-label="Permalink to &quot;**Planes Quirúrgicos Futuros:**&quot;">​</a></h3><ul><li>Eventual cierre de ileostomía (cuando condiciones lo permitan)</li><li>Reconstrucción del tránsito intestinal</li><li>Seguimiento a largo plazo de adherencias intraabdominales</li></ul><hr><h2 id="referencias-cruzadas" tabindex="-1">Referencias Cruzadas <a class="header-anchor" href="#referencias-cruzadas" aria-label="Permalink to &quot;Referencias Cruzadas&quot;">​</a></h2><ul><li><strong>Cronología:</strong> <a href="./../02-cronologia-clinica/">Cronología Clínica</a></li><li><strong>Complicaciones:</strong> <a href="./../05-evaluaciones/">Evaluación</a></li><li><strong>Medicamentos:</strong> <a href="./protocolos-medicacion.html">Protocolos de Medicación</a></li><li><strong>Soporte:</strong> <a href="./cuidados-soporte.html">Cuidados de Soporte</a></li></ul>',53)]))}const m=a(n,[["render",t]]);export{h as __pageData,m as default};
