import{_ as e,c as i,o,ag as n}from"./chunks/framework.CDAiG77C.js";const p=JSON.parse('{"title":"Supportive Care Protocols","description":"","frontmatter":{},"headers":[],"relativePath":"modulos-clinicos/04-tratamientos/cuidados-soporte.md","filePath":"modulos-clinicos/04-tratamientos/cuidados-soporte.md"}'),r={name:"modulos-clinicos/04-tratamientos/cuidados-soporte.md"};function t(l,a,s,u,c,d){return o(),i("div",null,a[0]||(a[0]=[n('<h1 id="supportive-care-protocols" tabindex="-1">Supportive Care Protocols <a class="header-anchor" href="#supportive-care-protocols" aria-label="Permalink to &quot;Supportive Care Protocols&quot;">​</a></h1><h2 id="patient-overview" tabindex="-1">Patient Overview <a class="header-anchor" href="#patient-overview" aria-label="Permalink to &quot;Patient Overview&quot;">​</a></h2><p><strong>Patient:</strong> Dylan Antonio Martinez Labastida<br><strong>Age:</strong> 8 years<br><strong>Weight:</strong> 37.70kg → 33.70kg (critical phase)</p><hr><h2 id="respiratory-support" tabindex="-1">Respiratory Support <a class="header-anchor" href="#respiratory-support" aria-label="Permalink to &quot;Respiratory Support&quot;">​</a></h2><h3 id="phase-i-pre-intubation" tabindex="-1">Phase I: Pre-Intubation <a class="header-anchor" href="#phase-i-pre-intubation" aria-label="Permalink to &quot;Phase I: Pre-Intubation&quot;">​</a></h3><p><strong>Status:</strong> Sin deterioro ventilatorio</p><ul><li>Paciente estable respiratoriamente al ingreso</li><li>Campos pulmonares ventilados bilateralmente</li></ul><h3 id="phase-ii-intraoperative-intubation-july-6-2025" tabindex="-1">Phase II: Intraoperative Intubation (July 6, 2025) <a class="header-anchor" href="#phase-ii-intraoperative-intubation-july-6-2025" aria-label="Permalink to &quot;Phase II: Intraoperative Intubation (July 6, 2025)&quot;">​</a></h3><p><strong>Intubation Details:</strong></p><ul><li><strong>Cánula:</strong> 5.5 FR con globo</li><li><strong>Posición:</strong> Fija en 16.5 cm de arcada dental</li><li><strong>Técnica:</strong> Al primer intento (anestesiología)</li><li><strong>Indicación:</strong> Cirugía mayor + inestabilidad hemodinámica</li></ul><h3 id="phase-iii-mechanical-ventilation-intensive-phase" tabindex="-1">Phase III: Mechanical Ventilation - Intensive Phase <a class="header-anchor" href="#phase-iii-mechanical-ventilation-intensive-phase" aria-label="Permalink to &quot;Phase III: Mechanical Ventilation - Intensive Phase&quot;">​</a></h3><p><strong>Ventilator Settings (July 6-7, 2025):</strong></p><ul><li><strong>Modo:</strong> SIMV (Synchronized Intermittent Mandatory Ventilation)</li><li><strong>FiO2:</strong> 35% → 45% → 35% (ajuste por hiperoxemia)</li><li><strong>Volumen tidal:</strong> 7.4ml/kg → 5ml/kg</li><li><strong>PEEP:</strong> 5 cmH2O → 6 cmH2O</li><li><strong>Frecuencia respiratoria:</strong> 18 rpm</li><li><strong>I:E:</strong> 1:2.3</li><li><strong>Oximetría:</strong> &gt;94% mantenida</li></ul><p><strong>Monitoring:</strong></p><ul><li>Gasometrías arteriales frecuentes</li><li>Ajuste de FiO2 por hiperoxemia</li><li>Manejo dinámico del ventilador</li></ul><h3 id="phase-iii-controlled-mechanical-ventilation-july-8-12-2025" tabindex="-1">Phase III: Controlled Mechanical Ventilation (July 8-12, 2025) <a class="header-anchor" href="#phase-iii-controlled-mechanical-ventilation-july-8-12-2025" aria-label="Permalink to &quot;Phase III: Controlled Mechanical Ventilation (July 8-12, 2025)&quot;">​</a></h3><p><strong>Ventilator Settings:</strong></p><ul><li><strong>Modo:</strong> CMV (Controlled Mechanical Ventilation) → Controlado por volumen</li><li><strong>FiO2:</strong> 60% → 90% (según requerimientos)</li><li><strong>Volumen tidal:</strong> 5.5ml/kg</li><li><strong>PEEP:</strong> 6 cmH2O</li><li><strong>Limitado por flujo</strong></li></ul><p><strong>Clinical Findings:</strong></p><ul><li>Campos pulmonares bien ventilados</li><li>Murmullo vesicular presente bilateralmente</li><li>Sin estertores ni sibilancias</li><li>Sin síndrome pleuropulmonar inicial</li></ul><h3 id="phase-iv-weaning-preparation-july-13-2025" tabindex="-1">Phase IV: Weaning Preparation (July 13, 2025) <a class="header-anchor" href="#phase-iv-weaning-preparation-july-13-2025" aria-label="Permalink to &quot;Phase IV: Weaning Preparation (July 13, 2025)&quot;">​</a></h3><p><strong>Ventilator Transition:</strong></p><ul><li><strong>Modo:</strong> Modalidad espontánea (preparación para extubación)</li><li>Pruebas de ventilación espontánea</li><li>Evaluación de drive respiratorio</li></ul><h3 id="phase-v-successful-extubation-july-13-2025" tabindex="-1">Phase V: Successful Extubation (July 13, 2025) <a class="header-anchor" href="#phase-v-successful-extubation-july-13-2025" aria-label="Permalink to &quot;Phase V: Successful Extubation (July 13, 2025)&quot;">​</a></h3><p><strong>Extubation Process:</strong></p><ul><li><strong>Extubación programada exitosa</strong></li><li><strong>Progresión:</strong> Fase I con mascarilla reservorio</li><li><strong>Complicaciones menores:</strong><ul><li>Estridor transitorio post-extubación</li><li>Sibilancias espiratorias transitorias</li><li>Remisión espontánea</li></ul></li></ul><p><strong>Post-Extubation Support:</strong></p><ul><li>Oxigenoterapia con mascarilla reservorio</li><li>Monitoreo estrecho de saturación</li><li>Vigilancia de signos de falla respiratoria</li></ul><h3 id="phase-vi-respiratory-independence-july-14-2025" tabindex="-1">Phase VI: Respiratory Independence (July 14, 2025) <a class="header-anchor" href="#phase-vi-respiratory-independence-july-14-2025" aria-label="Permalink to &quot;Phase VI: Respiratory Independence (July 14, 2025)&quot;">​</a></h3><p><strong>Current Status:</strong></p><ul><li>Tolerando extubación adecuadamente</li><li>Campos pulmonares con buena entrada y salida de aire</li><li>Murmullo vesicular presente aunque disminuido</li><li><strong>Complicación:</strong> Derrame pleural bilateral (manejado con furosemide)</li></ul><hr><h2 id="hemodynamic-support" tabindex="-1">Hemodynamic Support <a class="header-anchor" href="#hemodynamic-support" aria-label="Permalink to &quot;Hemodynamic Support&quot;">​</a></h2><h3 id="pre-operative-stability" tabindex="-1">Pre-Operative Stability <a class="header-anchor" href="#pre-operative-stability" aria-label="Permalink to &quot;Pre-Operative Stability&quot;">​</a></h3><p><strong>Status:</strong> Hemodinámicamente estable al ingreso</p><ul><li>Signos vitales dentro de percentiles para edad</li><li>Sin datos de bajo gasto cardíaco</li></ul><h3 id="intraoperative-hemodynamic-crisis-july-6-2025" tabindex="-1">Intraoperative Hemodynamic Crisis (July 6, 2025) <a class="header-anchor" href="#intraoperative-hemodynamic-crisis-july-6-2025" aria-label="Permalink to &quot;Intraoperative Hemodynamic Crisis (July 6, 2025)&quot;">​</a></h3><p><strong>Crisis Management:</strong></p><ul><li><strong>Trigger:</strong> Cifras tensionales por debajo de percentil durante cirugía</li><li><strong>Initial Response:</strong> Carga con solución Hartmann 10ml/kg (sin respuesta)</li><li><strong>Vasopresor Support:</strong><ul><li>Norepinefrina: Máximo 1mcg/kg/min intraoperatorio</li><li>Posterior descenso a 0.1mcg/kg/min post-operatorio</li></ul></li></ul><h3 id="icu-hemodynamic-management-july-6-14-2025" tabindex="-1">ICU Hemodynamic Management (July 6-14, 2025) <a class="header-anchor" href="#icu-hemodynamic-management-july-6-14-2025" aria-label="Permalink to &quot;ICU Hemodynamic Management (July 6-14, 2025)&quot;">​</a></h3><h4 id="phase-1-acute-support-july-6-7-2025" tabindex="-1"><strong>Phase 1: Acute Support (July 6-7, 2025)</strong> <a class="header-anchor" href="#phase-1-acute-support-july-6-7-2025" aria-label="Permalink to &quot;**Phase 1: Acute Support (July 6-7, 2025)**&quot;">​</a></h4><p><strong>Vasopresor Protocol:</strong></p><ul><li><strong>Noradrenalina:</strong> 0.3mcg/kg/min</li><li><strong>Rationale:</strong> Tensiones arteriales sobre percentil</li><li><strong>Adjustment:</strong> Descenso progresivo a 0.1mcg/kg/min</li><li><strong>Monitoring:</strong> Frecuencias cardíacas y tensiones arteriales dentro de percentiles</li></ul><p><strong>Clinical Assessment:</strong></p><ul><li>Sin datos de bajo gasto cardíaco</li><li>Precordio con ruidos cardíacas rítmicos de buen tono e intensidad</li><li>No se auscultan soplos</li><li>Pulsos periféricos presentes y de adecuada intensidad</li><li>Llenado capilar: 2 segundos</li></ul><h4 id="phase-2-transition-to-epinephrine-july-7-12-2025" tabindex="-1"><strong>Phase 2: Transition to Epinephrine (July 7-12, 2025)</strong> <a class="header-anchor" href="#phase-2-transition-to-epinephrine-july-7-12-2025" aria-label="Permalink to &quot;**Phase 2: Transition to Epinephrine (July 7-12, 2025)**&quot;">​</a></h4><p><strong>Medication Transition:</strong></p><ul><li><strong>Adrenalina:</strong> Cambio de noradrenalina</li><li><strong>Dosing Evolution:</strong><ul><li>0.5mcg/kg/min → 0.04mcg/kg/min → 0.03mcg/kg/min</li></ul></li><li><strong>Clinical Response:</strong> Estabilización progresiva</li></ul><p><strong>Hemodynamic Monitoring:</strong></p><ul><li>Precordio normodinámico, normofoneético</li><li>Pulsos centrales y periféricos presentes</li><li>Llenado capilar: 2 segundos mantenido</li><li>Balance hídrico negativo con uresis conservada</li></ul><h4 id="phase-3-weaning-july-12-14-2025" tabindex="-1"><strong>Phase 3: Weaning (July 12-14, 2025)</strong> <a class="header-anchor" href="#phase-3-weaning-july-12-14-2025" aria-label="Permalink to &quot;**Phase 3: Weaning (July 12-14, 2025)**&quot;">​</a></h4><p><strong>Successful Weaning:</strong></p><ul><li>Suspensión progresiva de soporte vasopresor</li><li>Mantenimiento de estabilidad hemodinámica</li><li>Sin datos de bajo gasto cardíaco</li><li>Sin respuesta inflamatoria sistémica</li></ul><hr><h2 id="fluid-and-electrolyte-management" tabindex="-1">Fluid and Electrolyte Management <a class="header-anchor" href="#fluid-and-electrolyte-management" aria-label="Permalink to &quot;Fluid and Electrolyte Management&quot;">​</a></h2><h3 id="acute-phase-fluid-management-july-6-8-2025" tabindex="-1">Acute Phase Fluid Management (July 6-8, 2025) <a class="header-anchor" href="#acute-phase-fluid-management-july-6-8-2025" aria-label="Permalink to &quot;Acute Phase Fluid Management (July 6-8, 2025)&quot;">​</a></h3><h4 id="initial-fluid-requirements" tabindex="-1"><strong>Initial Fluid Requirements:</strong> <a class="header-anchor" href="#initial-fluid-requirements" aria-label="Permalink to &quot;**Initial Fluid Requirements:**&quot;">​</a></h4><ul><li><strong>Líquidos IV totales:</strong> 2000ml/m²SC/día → 1800ml/m²SC/día</li><li><strong>Esquema de soluciones:</strong> 860/50/40 → 1000/50/40/100/50 GKM 1.4</li></ul><h4 id="electrolyte-corrections" tabindex="-1"><strong>Electrolyte Corrections:</strong> <a class="header-anchor" href="#electrolyte-corrections" aria-label="Permalink to &quot;**Electrolyte Corrections:**&quot;">​</a></h4><ul><li><strong>Hipokalemia:</strong> Aporte total 60mEq/m²SC/día</li><li><strong>Hipoalbuminemia:</strong> Albumina 1gr/kg/dosis cada 24 horas (nivel inicial 1.9)</li></ul><h3 id="fluid-optimization-phase-july-8-12-2025" tabindex="-1">Fluid Optimization Phase (July 8-12, 2025) <a class="header-anchor" href="#fluid-optimization-phase-july-8-12-2025" aria-label="Permalink to &quot;Fluid Optimization Phase (July 8-12, 2025)&quot;">​</a></h3><h4 id="diuretic-protocol" tabindex="-1"><strong>Diuretic Protocol:</strong> <a class="header-anchor" href="#diuretic-protocol" aria-label="Permalink to &quot;**Diuretic Protocol:**&quot;">​</a></h4><ul><li><strong>Furosemide:</strong> 0.5mg/kg/dosis → infusión continua → 10mg cada 8 horas</li><li><strong>Indication:</strong> Sobrecarga hídrica, edema en extremidades inferiores y genital</li><li><strong>Monitoring:</strong> Volúmenes urinarios, balance hídrico</li></ul><h4 id="balance-management" tabindex="-1"><strong>Balance Management:</strong> <a class="header-anchor" href="#balance-management" aria-label="Permalink to &quot;**Balance Management:**&quot;">​</a></h4><ul><li><strong>Uresis:</strong> 2.1ml/kg/24h → 1.2ml/kg/hr media</li><li><strong>Balance hídrico:</strong> Progresión a balance negativo (-118ml)</li><li><strong>Proyección de líquidos:</strong> 1512ml/SC/día</li></ul><h3 id="recovery-phase-july-13-14-2025" tabindex="-1">Recovery Phase (July 13-14, 2025) <a class="header-anchor" href="#recovery-phase-july-13-14-2025" aria-label="Permalink to &quot;Recovery Phase (July 13-14, 2025)&quot;">​</a></h3><h4 id="fluid-normalization" tabindex="-1"><strong>Fluid Normalization:</strong> <a class="header-anchor" href="#fluid-normalization" aria-label="Permalink to &quot;**Fluid Normalization:**&quot;">​</a></h4><ul><li>Suspensión de diurético con horario</li><li>Uresis espontánea adecuada</li><li>Resolución de edemas</li><li><strong>Complicación:</strong> Derrame pleural bilateral (tratado con furosemide PRN)</li></ul><hr><h2 id="nutritional-support" tabindex="-1">Nutritional Support <a class="header-anchor" href="#nutritional-support" aria-label="Permalink to &quot;Nutritional Support&quot;">​</a></h2><h3 id="acute-phase-complete-bowel-rest-july-6-12-2025" tabindex="-1">Acute Phase: Complete Bowel Rest (July 6-12, 2025) <a class="header-anchor" href="#acute-phase-complete-bowel-rest-july-6-12-2025" aria-label="Permalink to &quot;Acute Phase: Complete Bowel Rest (July 6-12, 2025)&quot;">​</a></h3><p><strong>Nutritional Status:</strong></p><ul><li><strong>Ayuno completo</strong> por abdomen abierto y fístula enterocutánea</li><li><strong>Sonda nasogástrica:</strong> Derivación con gasto biliar moderado</li><li><strong>Esquema de soluciones:</strong> Soporte hidroelectrolítico básico</li></ul><h3 id="metabolic-support-implementation-july-8-12-2025" tabindex="-1">Metabolic Support Implementation (July 8-12, 2025) <a class="header-anchor" href="#metabolic-support-implementation-july-8-12-2025" aria-label="Permalink to &quot;Metabolic Support Implementation (July 8-12, 2025)&quot;">​</a></h3><p><strong>Parenteral Nutrition:</strong></p><ul><li><strong>Nutrición parenteral total (NPT):</strong> Basal</li><li><strong>Protein Support:</strong> Albumina 1gr/kg/día por hipoalbuminemia</li><li><strong>Monitoring:</strong> Parámetros basales, equilibrio metabólico</li></ul><h3 id="enteral-stimulation-phase-july-14-2025" tabindex="-1">Enteral Stimulation Phase (July 14, 2025) <a class="header-anchor" href="#enteral-stimulation-phase-july-14-2025" aria-label="Permalink to &quot;Enteral Stimulation Phase (July 14, 2025)&quot;">​</a></h3><p><strong>Transition to Enteral Nutrition:</strong></p><ul><li><strong>Inicio de estímulo enteral</strong> con dieta líquida</li><li><strong>Progression:</strong> Gradual según tolerancia</li><li><strong>Monitoring:</strong> Tolerancia gastrointestinal, gasto por ileostomía</li></ul><hr><h2 id="gastrointestinal-support" tabindex="-1">Gastrointestinal Support <a class="header-anchor" href="#gastrointestinal-support" aria-label="Permalink to &quot;Gastrointestinal Support&quot;">​</a></h2><h3 id="gastric-protection" tabindex="-1">Gastric Protection <a class="header-anchor" href="#gastric-protection" aria-label="Permalink to &quot;Gastric Protection&quot;">​</a></h3><p><strong>Proton Pump Inhibitor Protocol:</strong></p><ul><li><strong>Omeprazol:</strong> 1mg/kg/dosis</li><li><strong>Duration:</strong> Continuo durante toda la estancia</li><li><strong>Indication:</strong> Prevención de úlcera por estrés en paciente crítico</li></ul><h3 id="abdominal-decompression" tabindex="-1">Abdominal Decompression <a class="header-anchor" href="#abdominal-decompression" aria-label="Permalink to &quot;Abdominal Decompression&quot;">​</a></h3><p><strong>Nasogastric Drainage:</strong></p><ul><li><strong>Sonda nasogástrica:</strong> Derivación continua</li><li><strong>Output:</strong> Gasto biliar en moderada cantidad</li><li><strong>Monitoring:</strong> Volumen y características del gasto</li></ul><h3 id="bowel-management" tabindex="-1">Bowel Management <a class="header-anchor" href="#bowel-management" aria-label="Permalink to &quot;Bowel Management&quot;">​</a></h3><p><strong>Ileostomy Care:</strong></p><ul><li><strong>Ileostomía:</strong> Bordes rosados, sin compromiso vascular</li><li><strong>Function:</strong> Gasto controlado</li><li><strong>Skin Protection:</strong> Cuidados especializados de estoma</li></ul><hr><h2 id="renal-support-and-monitoring" tabindex="-1">Renal Support and Monitoring <a class="header-anchor" href="#renal-support-and-monitoring" aria-label="Permalink to &quot;Renal Support and Monitoring&quot;">​</a></h2><h3 id="renal-function-assessment" tabindex="-1">Renal Function Assessment <a class="header-anchor" href="#renal-function-assessment" aria-label="Permalink to &quot;Renal Function Assessment&quot;">​</a></h3><p><strong>Baseline Function:</strong></p><ul><li>Sin datos de lesión renal aguda por bioquímicos</li><li><strong>Uresis:</strong> 2.1ml/kg/24h inicialmente</li><li><strong>Creatinina:</strong> Dentro de límites normales</li></ul><h3 id="fluid-balance-management" tabindex="-1">Fluid Balance Management <a class="header-anchor" href="#fluid-balance-management" aria-label="Permalink to &quot;Fluid Balance Management&quot;">​</a></h3><p><strong>Diuretic Support:</strong></p><ul><li><strong>Furosemide:</strong> Forzamiento diurético controlado</li><li><strong>Albumina:</strong> Reposición simultánea para mantener presión oncótica</li><li><strong>Monitoring:</strong> Balance hídrico estricto</li></ul><h3 id="edema-management" tabindex="-1">Edema Management <a class="header-anchor" href="#edema-management" aria-label="Permalink to &quot;Edema Management&quot;">​</a></h3><p><strong>Clinical Findings:</strong></p><ul><li><strong>Edema:</strong> + en extremidades inferiores y genital (fase aguda)</li><li><strong>Resolution:</strong> Progresiva con manejo diurético</li><li><strong>Llenado capilar:</strong> Mantenido en 2 segundos</li></ul><hr><h2 id="icu-general-care-protocols" tabindex="-1">ICU General Care Protocols <a class="header-anchor" href="#icu-general-care-protocols" aria-label="Permalink to &quot;ICU General Care Protocols&quot;">​</a></h2><h3 id="infection-prevention" tabindex="-1">Infection Prevention <a class="header-anchor" href="#infection-prevention" aria-label="Permalink to &quot;Infection Prevention&quot;">​</a></h3><p><strong>Environmental Controls:</strong></p><ul><li>Cuidados generales de enfermería</li><li>Cuidados específicos de sondas y catéteres</li><li>Asepsia estricta en procedimientos</li></ul><h3 id="monitoring-protocols" tabindex="-1">Monitoring Protocols <a class="header-anchor" href="#monitoring-protocols" aria-label="Permalink to &quot;Monitoring Protocols&quot;">​</a></h3><p><strong>Vital Signs:</strong></p><ul><li>Signos vitales cada hora</li><li>Vigilancia de curva térmica</li><li>Monitoreo cardiorrespiratorio continuo</li></ul><h3 id="specialized-care" tabindex="-1">Specialized Care <a class="header-anchor" href="#specialized-care" aria-label="Permalink to &quot;Specialized Care&quot;">​</a></h3><p><strong>Critical Care Standards:</strong></p><ul><li>Escala de sedación (RASS)</li><li>Prevención de úlceras por presión</li><li>Cuidados de la vía aérea</li><li>Manejo del abdomen abierto</li></ul><hr><h2 id="current-support-status-july-14-2025" tabindex="-1">Current Support Status (July 14, 2025) <a class="header-anchor" href="#current-support-status-july-14-2025" aria-label="Permalink to &quot;Current Support Status (July 14, 2025)&quot;">​</a></h2><h3 id="respiratory" tabindex="-1"><strong>Respiratory:</strong> <a class="header-anchor" href="#respiratory" aria-label="Permalink to &quot;**Respiratory:**&quot;">​</a></h3><ul><li>Extubado, tolerando aire ambiente</li><li>Vigilancia de derrame pleural bilateral</li></ul><h3 id="hemodynamic" tabindex="-1"><strong>Hemodynamic:</strong> <a class="header-anchor" href="#hemodynamic" aria-label="Permalink to &quot;**Hemodynamic:**&quot;">​</a></h3><ul><li>Estable sin soporte vasopresor</li><li>Hemodinámicamente compensado</li></ul><h3 id="nutritional" tabindex="-1"><strong>Nutritional:</strong> <a class="header-anchor" href="#nutritional" aria-label="Permalink to &quot;**Nutritional:**&quot;">​</a></h3><ul><li>Iniciando estímulo enteral</li><li>NPT de soporte</li></ul><h3 id="renal" tabindex="-1"><strong>Renal:</strong> <a class="header-anchor" href="#renal" aria-label="Permalink to &quot;**Renal:**&quot;">​</a></h3><ul><li>Función renal preservada</li><li>Diuréticos PRN para derrame pleural</li></ul><hr><h2 id="cross-references" tabindex="-1">Cross-References <a class="header-anchor" href="#cross-references" aria-label="Permalink to &quot;Cross-References&quot;">​</a></h2><ul><li><strong>Medications:</strong> <a href="./medication-protocols.html">Medication Protocols</a></li><li><strong>Surgery:</strong> <a href="./surgical-procedures.html">Surgical Procedures</a></li><li><strong>Monitoring:</strong> <a href="./../03-diagnostics/">Diagnostics</a></li><li><strong>Assessment:</strong> <a href="./../05-assessment/">Clinical Assessment</a></li></ul>',126)]))}const h=e(r,[["render",t]]);export{p as __pageData,h as default};
